import React, { useState } from "react";

function ChatbotInterface() {
  const [messages, setMessages] = useState([
    { text: "Hello! How can I help you today?", isUser: false },
  ]);
  const [inputValue, setInputValue] = useState("");

  // Handle sending messages
  const handleSendMessage = () => {
    if (inputValue.trim() === "") return;

    // Add user message
    setMessages((prevMessages) => [
      ...prevMessages,
      { text: inputValue, isUser: true },
    ]);

    // Clear input field
    setInputValue("");

    // Simulate chatbot response
    setTimeout(() => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: "I'm here to assist you!", isUser: false },
      ]);
    }, 1000);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: `Uploaded: ${file.name}`, isUser: true },
      ]);
    }
  };

  return (
    <div className="flex flex-col h-screen p-8">
      <h2 className="text-3xl font-bold mb-4">Chatbot Interface</h2>
      <div className="flex-grow bg-white shadow-lg rounded-lg p-4 flex flex-col">
        {/* Chat Window */}
        <div className="flex-grow overflow-y-scroll chatbot-window bg-gray-100 p-4 rounded-lg">
          {/* Display messages */}
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex items-center mb-4 ${
                message.isUser ? "justify-end" : "justify-start"
              }`}
            >
              {!message.isUser && (
                <img
                  src="/images/bot_icon.png"
                  alt="Chatbot Icon"
                  className="w-8 h-8 mr-3"
                />
              )}
              <div
                className={`p-3 rounded-lg shadow ${
                  message.isUser
                    ? "bg-white text-gray-800 border border-gray-300"
                    : "bg-blue-500 text-white"
                } max-w-xs`}
              >
                {message.text}
              </div>
              {message.isUser && (
                <img
                  src="/images/user_icon.png"
                  alt="User Icon"
                  className="w-8 h-8 ml-3"
                />
              )}
            </div>
          ))}
        </div>

        {/* User Input Section */}
        <div className="mt-4 flex items-center">
          {/* Upload Icon */}
          <label className="mr-4 cursor-pointer">
            <input
              type="file"
              className="hidden"
              onChange={handleFileUpload}
            />
            <img
              src="/images/upload_icon.png"
              alt="Upload Icon"
              className="w-8 h-8"
            />
          </label>

          {/* Input Field */}
          <input
            type="text"
            placeholder="Type your message..."
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-lg"
            onKeyPress={(e) => {
              if (e.key === "Enter") {
                handleSendMessage();
              }
            }}
          />

          {/* Send Button */}
          <button
            onClick={handleSendMessage}
            className="ml-4 bg-blue-500 text-white px-4 py-2 rounded-lg"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatbotInterface;
