import React, { useState } from "react";

function App() {
  // State to handle modal visibility and form data
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [file, setFile] = useState(null);
  const [googleScholarLink, setGoogleScholarLink] = useState("");
  const [userGoal, setUserGoal] = useState("");

  // Function to open modal
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Handle file upload
  const handleFileUpload = (event) => {
    const uploadedFile = event.target.files[0];
    setFile(uploadedFile);
  };

  // Handle Google Scholar Link Input
  const handleGoogleScholarChange = (event) => {
    setGoogleScholarLink(event.target.value);
  };

  // Handle User Goal Dropdown
  const handleUserGoalChange = (event) => {
    setUserGoal(event.target.value);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* App Title Banner */}
      <header className="bg-gradient-to-r from-blue-600 to-teal-400 text-white py-8 px-8 shadow-md">
        <div className="container mx-auto flex items-center justify-start space-x-6">
          {/* Logo */}
          <img src="/logo1.png" alt="App Logo" className="w-32 h-auto" />
          {/* App Name */}
          <h1 className="text-4xl md:text-5xl font-extrabold tracking-tight">Timeline of You</h1>
        </div>
      </header>

      {/* New 3-Column Image Grid for The American Dream */}
      <main className="container mx-auto py-16 px-4">
        {/* Section Title */}
        <h2 className="text-4xl font-bold text-gray-800 mb-12 text-center">The Pursuit of Happiness</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-10 mb-20">
          {/* Image 1 */}
          <div className="relative">
            <img
              src="/images/highlight_1.png"
              alt="Highlight 1"
              className="w-full h-80 object-cover object-center rounded-xl shadow-lg transition-transform duration-300 hover:scale-105"
            />
            <h3 className="absolute bottom-4 left-0 w-full bg-white bg-opacity-90 py-2 px-4 text-center text-gray-800 font-semibold text-lg">
              Chasing New Horizons
            </h3>
          </div>

          {/* Image 2 */}
          <div className="relative">
            <img
              src="/images/highlight_5.png"
              alt="Highlight 2"
              className="w-full h-80 object-cover object-center rounded-xl shadow-lg transition-transform duration-300 hover:scale-105"
            />
            <h3 className="absolute bottom-4 left-0 w-full bg-white bg-opacity-90 py-2 px-4 text-center text-gray-800 font-semibold text-lg">
              The Power of Habits
            </h3>
          </div>

          {/* Image 3 */}
          <div className="relative">
            <img
              src="/images/highlight_4.png"
              alt="Highlight 3"
              className="w-full h-80 object-cover object-center rounded-xl shadow-lg transition-transform duration-300 hover:scale-105"
            />
            <h3 className="absolute bottom-4 left-0 w-full bg-white bg-opacity-90 py-2 px-4 text-center text-gray-800 font-semibold text-lg">
              Overcoming Setbacks
            </h3>
          </div>
        </div>

        {/* Shape Your Future Section */}
        <div className="mt-20 text-center">
          {/* Heading for Success Image */}
          <h2 className="text-4xl font-bold text-gray-800 mb-6">Shape Your Future, One Step at a Time</h2>

          {/* Success Images Grid */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 justify-center">
            {/* Image 1 */}
            <div className="inline-block bg-white shadow-lg rounded-xl overflow-hidden transform hover:scale-105 transition-transform duration-300">
              <img
                src="/images/success_image1.png" // Replace with your first success image
                alt="Success Image 1"
                className="w-full h-auto object-cover"
              />
            </div>

            {/* Image 2 */}
            <div className="inline-block bg-white shadow-lg rounded-xl overflow-hidden transform hover:scale-105 transition-transform duration-300">
              <img
                src="/images/success_image2.png" // Replace with your second success image
                alt="Success Image 2"
                className="w-full h-auto object-cover"
              />
            </div>

            {/* Image 3 */}
            <div className="inline-block bg-white shadow-lg rounded-xl overflow-hidden transform hover:scale-105 transition-transform duration-300">
              <img
                src="/images/success_image3.png" // Replace with your third success image
                alt="Success Image 3"
                className="w-full h-auto object-cover"
              />
            </div>
          </div>

          {/* Start Your Journey Button */}
          <div className="mt-6">
            <button
              onClick={openModal} // Open modal on button click
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-4 px-8 rounded-lg transition-transform transform hover:scale-105"
            >
              Start Your Journey
            </button>
          </div>
        </div>

        {/* Navigating the Complex Road Section */}
        <h2 className="text-4xl font-bold text-gray-800 mb-12 text-center">Navigating the Complex Road</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-10 mb-20">
          <div className="relative">
            <img
              src="/images/image_1.png"
              alt="Time Management"
              className="w-full h-64 object-cover object-center rounded-xl shadow-lg transition-transform duration-300 hover:scale-105"
            />
            <h3 className="absolute bottom-4 left-0 w-full bg-white bg-opacity-90 py-2 px-4 text-center text-gray-800 font-semibold text-lg">
              Time Management
            </h3>
          </div>

          {/* Additional Grid Items... */}
        </div>
      </main>

      {/* Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white rounded-lg shadow-lg w-full max-w-2xl p-8 relative">
            <button
              onClick={closeModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-700 focus:outline-none"
            >
              &times;
            </button>
            <h2 className="text-3xl font-extrabold mb-4 text-center">Start Your Journey</h2>
            <p className="text-gray-700 text-center mb-8">
              Begin your journey by providing the following details.
            </p>

            {/* Google Scholar Link Field */}
            <div className="mb-8">
              <div className="flex items-center mb-4">
                <img src="/images/link_icon.png" alt="Link Icon" className="w-6 h-6 mr-3" />
                <h3 className="font-bold text-lg">Google Scholar Link</h3>
              </div>
              <input
                type="text"
                value={googleScholarLink}
                onChange={handleGoogleScholarChange}
                placeholder="Enter your Google Scholar link"
                className="w-full py-2 px-4 border border-gray-300 rounded-lg"
              />
            </div>

            {/* File Upload Section */}
            <div className="mb-8">
              <div className="flex items-center mb-4">
                <img src="/images/file_icon.png" alt="File Icon" className="w-6 h-6 mr-3" />
                <h3 className="font-bold text-lg">Upload Zip File</h3>
              </div>
              <input
                type="file"
                accept=".zip"
                onChange={handleFileUpload}
                className="py-2 px-4 border border-gray-300 rounded-lg cursor-pointer"
              />
            </div>

            {/* Modal Footer */}
            <div className="text-center mt-8">
              <button
                onClick={closeModal}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-lg transition-transform transform hover:scale-105"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
