import React from "react";
import { useLocation } from "react-router-dom";

function VisualizationDashboard() {
  const location = useLocation();
  const { googleScholarLink, userGoal, file } = location.state || {};

  return (
    <div className="min-h-screen bg-white">
      <header className="bg-gradient-to-r from-blue-600 to-teal-400 text-white py-8 px-8 shadow-md">
        <div className="container mx-auto flex items-center justify-start space-x-6">
          <img src="/logo2.png" alt="Dashboard Logo" className="w-32 h-auto" />
          <h1 className="text-4xl md:text-5xl font-extrabold tracking-tight">Visualization Dashboard</h1>
        </div>
      </header>

      <main className="container mx-auto py-16 px-4">
        <h2 className="text-3xl font-bold text-gray-800 mb-8">Your Data Overview</h2>

        <div className="mb-8">
          <h3 className="text-xl font-semibold mb-4">Google Scholar Link</h3>
          <p>{googleScholarLink || "No link provided"}</p>
        </div>

        <div className="mb-8">
          <h3 className="text-xl font-semibold mb-4">User Goal</h3>
          <p>{userGoal || "No goal provided"}</p>
        </div>

        <div className="mb-8">
          <h3 className="text-xl font-semibold mb-4">Uploaded File</h3>
          <p>{file ? file.name : "No file uploaded"}</p>
        </div>

        {/* Add your chart visualizations here */}
      </main>
    </div>
  );
}

export default VisualizationDashboard;
