import React, { useState } from "react";
import ChatbotInterface from './ChatbotInterface'; // Importing Chatbot component

function VisualizationDashboard() {
  // State to manage the active tab (timeline or chatbot)
  const [activeTab, setActiveTab] = useState('timeline');

  // Sample chart content (can be replaced with actual chart code)
  const renderTimelineDashboard = () => (
    <div className="p-8">
      <h2 className="text-3xl font-bold mb-4">Timeline Dashboard</h2>
      <div className="bg-white shadow-lg rounded-lg p-4">
        {/* Placeholder for a sample chart */}
        <h3 className="text-xl font-semibold mb-4">Chart Title</h3>
        <img src="/images/sample_chart.png" alt="Sample Chart" className="w-full h-64 object-contain" />
      </div>
    </div>
  );

  // Render content based on the active tab
  const renderContent = () => {
    if (activeTab === 'timeline') {
      return renderTimelineDashboard();
    }
    return <ChatbotInterface />; // Rendering Chatbot Interface
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Title Banner - Same as the main page */}
      <header className="bg-gradient-to-r from-blue-600 to-teal-400 text-white py-8 px-8 shadow-md">
        <div className="container mx-auto flex items-center justify-start space-x-6">
          <img src="/logo1.png" alt="App Logo" className="w-32 h-auto" />
          <h1 className="text-4xl md:text-5xl font-extrabold tracking-tight">Visualization Dashboard</h1>
        </div>
      </header>

      {/* Tabs */}
      <div className="container mx-auto py-4 px-4">
        <div className="flex border-b-2 border-gray-200">
          <button
            className={`py-2 px-4 text-lg font-semibold ${
              activeTab === 'timeline' ? 'text-blue-600 border-b-4 border-blue-600' : 'text-gray-600'
            }`}
            onClick={() => setActiveTab('timeline')}
          >
            Timeline Dashboard
          </button>
          <button
            className={`py-2 px-4 text-lg font-semibold ml-4 ${
              activeTab === 'chatbot' ? 'text-blue-600 border-b-4 border-blue-600' : 'text-gray-600'
            }`}
            onClick={() => setActiveTab('chatbot')}
          >
            Chatbot Interface
          </button>
        </div>

        {/* Content Area */}
        <div className="mt-8">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default VisualizationDashboard;
